    ethereal: {
      style: 'artistic' as const,
      hairColor: 'platinum',
      eyeColor: 'blue',
      skinTone: 'fair',
      outfit: 'intimate' as const,
      expression: 'mysterious' as const,
      background: 'abstract' as const,
      lighting: 90,
      glow: 80
